import React from "react";
import CornerIcon from "./CornerIcon";
import Feed from "./Feed";
import { Dropdown, Item } from "semantic-ui-react";

const friendOptions = [
  {
    key: "Jenny Hess",
    text: "Jenny Hess",
    image: { avatar: true, src: "https://picsum.photos/40/40" },
  },
  {
    key: "Elliot Fu",
    content: "Elliot Fu \n No me fsf",
    value: "Elliot Fu",
    image: { avatar: true, src: "https://picsum.photos/60/60" },
  },
];

const Notification = () => (
  <div>
    <Feed />

    <Dropdown icon={<CornerIcon />}>
      <Dropdown.Menu>
        <Dropdown.Header content="New messages from " />
        {/* <Dropdown.Item as={<Feed />} /> */}
        <Dropdown.Item text="Rename" description="ctrl + r" />
        {friendOptions.map((option) => (
          <Dropdown.Item key={option.value} {...option} />
        ))}
        <Dropdown.Item text="Make a copy">
          <Item>
            <Item.Image
              size="tiny"
              src="https://react.semantic-ui.com/images/wireframe/image.png"
            />

            <Item.Content>
              <Item.Header as="a">Header</Item.Header>
              <Item.Meta>Description</Item.Meta>
              <Item.Description>
                <Image src="https://react.semantic-ui.com/images/wireframe/short-paragraph.png" />
              </Item.Description>
              <Item.Extra>Additional Details</Item.Extra>
            </Item.Content>
          </Item>
        </Dropdown.Item>
        <Dropdown.Item icon="folder" text="Move to folder" />
        <Dropdown.Item icon="trash" text="Move to trash" />
        <Dropdown.Divider />
        <Dropdown.Item text="Download As..." />
        <Dropdown.Item text="Publish To Web" />
        <Dropdown.Item text="E-mail Collaborators" />
      </Dropdown.Menu>
    </Dropdown>
  </div>
);

export default Notification;
