import React from "react";
import {Chart} from 'chart.js';
import generateYearArray from '../../../utils/generateYearArray';
import {generateRandomNumberArray} from '../../../utils/generateRandomNumber';
const SIZE = 5;
class BarChart extends React.Component {
    chartRef = React.createRef();

  componentDidMount = () => {
    this.renderChart();
  };

  renderChart = () => {
    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: "bar",
      data: {
        labels: generateYearArray(SIZE),
        datasets: [
          {
            label: "dollar (thousand)",
            backgroundColor: [
              "#3e95cd",
              "#8e5ea2",
              "#3cba9f",
              "#e8c3b9",
              "#c45850",
            ],
            data: generateRandomNumberArray(SIZE, 1000),
          },
        ],
      },
      options: {
        legend: { display: false },
        title: {
          display: true,
          text: this.props.chartTitle,
        },
      },
    });
  };

  render() {
    return ( 
      <div style={{marginTop:'15px'}}>
        <h5 className="no-margin">{this.props.text}</h5>
        <canvas
          className="gray-background"
          style={{ paddingBottom: "15px" }}
          id="myChart"
          ref={this.chartRef}
        />
      </div>
    );
  }
}

export default BarChart;
