    //       ],
    //     },
    //   },
    // });

    this.updateTimeSerieEveryXSeconds();
  }

  updateTimeSerieEveryXSeconds = () => {
    setInterval(() => {
      let newData = Math.floor(Math.random() * 50);
      let newDatArray = [...this.state.data, newData];
      this.setState({ data: newDatArray });

      let newLabel = Math.floor(Math.random() * 10);
      let newLabelArray = [...this.state.labels, newLabel];
      this.setState({ labels: newLabelArray });

      const myChartRef = this.chartRef.current.getContext("2d");

      new Chart(myChartRef, {
        type: "line",
        data: {
          //Bring in data
          labels: this.state.labels,
          datasets: [
            {
              borderColor: "red",
              showLine: true,
              fill: false,
              label: "Customer in store",
              data: this.state.data,
            },
          ],
        },
        options: {
          scales: {
            yAxes: [
              {
                stacked: true,
                ticks: {
                  suggestedMin: 0,
                  suggestedMax: 100,
                  maxTicksLimit: 5,
                  beginAtZero: true,
                },
              },
            ],
            xAxes: [],
          },
        },
      });
    }, 2000);
  };

  render() {
    return (
      <div>
        <canvas id="myChart" ref={this.chartRef} />
      </div>
    );
  }
}

export default TimeSerie;
