import axios from 'axios';
import React from 'react';
import {Chart} from 'chart.js';

class TimeSerie extends React.Component {
    constructor(props) {
        super(props);
        this.timeSerie = React.createRef();
    }

    componentDidMount = async () => {
        const data = await axios.get('https://gw.alipayobjects.com/os/antvdemo/assets/data/fireworks-sales.json');
        console.log(data);
        this.setState({data : data});
    }

    renderChart = () => {
        let data = [{
            x: 10,
            y: 20
        }, {
            x: 15,
            y: 10
        }]
        this.timeSerie  = new Chart(this.timeSerie, {
            type: 'line',
            data: {
                data : [{
                    x: 10,
                    y: 20
                }, {
                    x: 15,
                    y: 10
                }] 
            },
            options: {
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true
                        }
                    }]
                }
            }
        });
    }

    render() {
        return (
            <div>
                {this.renderChart()}
            </div>
        )
    }
}

export default TimeSerie;
