    src : "https://react.semantic-ui.com/images/avatar/small/rachel.png",
    name : "Anna Schznerei",
    icon : {
      name : "circle red",
      status : "Offline"
    },
    message : "We are having an unexpected large food order. We need you to confirm."
  }, {
    src : "https://react.semantic-ui.com/images/avatar/small/lindsay.png",
    name : "Jane Doe",
    icon : {
      name : "circle green",
      status : "Online"
    },
    message : "Your business plan for the next month was approved by the counsel. Please come to my office and fill out essential documents "
  }
]
export default () => {
  return (
    <Grid divided="vertically" stackable>
      <Grid.Row columns={1}>
        <Grid.Column>
          <div className="message">
            <div className="message-title">Chat message</div>
            <ul className="message-list">
              {users.map(user => {
                <li className="message-item">
                <Image
                  className="custom-avatar"
                  src={user.src}
                />
                <div className="message-chat">
                  <div className="message-chat-username">
              {user.name}  <Icon name={user.icon.name}>{user.icon.status}</Icon>
                  </div>
              <span>{user.message}</span>
                </div>
              </li>
              })}
