import React from "react";
import { List, Icon } from "semantic-ui-react";

class TodoList extends React.Component {
    state = {
        clicked : false
    }

    handleClick = () => {
        this.setState({clicked : !this.state.clicked})
    }

  render() {
      let clicked = this.state.clicked;
    return (
      <div className="todo-container border">
        <div className="reminder-header">
          <h4 className="no-margin" style={{ display: "inline-block" }}>
            Reminder
          </h4>
          <Icon name="sort" className="reminder-icon" />
          <Icon name="edit outline" className="reminder-icon" />
        </div>

        <List>
          <List.Item onClick={this.handleClick} className="reminder-item">
            <List.Icon name={clicked ? 'check' : 'cancel'} color={clicked ? 'green' : 'red'} size="normal" />
            <List.Content className={clicked ? 'check' : 'cancel'}>
              Signing new business plan{" "}
              <span className="time-reminder">5 days</span>
            </List.Content>
          </List.Item>

          <List.Item>
            <List.Icon name="cancel" color="red" size="normal" />
            <List.Content>Signing new business plan</List.Content>
          </List.Item>
        </List>
      </div>
    );
  }
}

export default TodoList;
