import React from "react";
import { Statistic, Icon } from "semantic-ui-react";
import '../../styles/Statistic.css'
class ChangableStatistic extends React.Component {
  //Change the default number here
  state = {
    count: 1000,
  };

  componentDidMount = () => {
    this.updateCount();
  };

  //Increase number randomly every 2 seconds
  updateCount = () => {
    let newCount;
    setInterval(() => {
      newCount = Math.floor(this.state.count + Math.random(30) * 10);
      this.setState({ count: newCount });
    }, 2000);
  };

  renderContent = () => {

    const {icon} = this.props;
    console.log('icon is', icon);

    return (
      <div className="statistic-container">
        <Icon name={icon.name} size='huge' color={icon.color}/>
        <Statistic horizontal className="number-text">
          <Statistic.Value>{this.state.count}</Statistic.Value>
          <Statistic.Label>{this.props.text}</Statistic.Label>
        </Statistic>
      </div>
    );
  };
  render() {
    return this.renderContent();
  }
}

export default ChangableStatistic;
