import React from "react";
import { Statistic } from "semantic-ui-react";

class ChangableStatistic extends React.Component {
  //Change the default number here
  state = {
    count : 1000
  }

  componentDidMount = () => {
    this.updateCount();
  }

  //Increase number randomly every 2 seconds
  updateCount = () => {

    setInterval(() => {
      let newCount;
      newCount = Math.floor(this.state.count += Math.random(30));
      console.log(newCount)
      this.setState({count : newCount})
      console.log(this.state.count)
    }, 2000)
  }

  renderContent = () => {
    return (
      <Statistic horizontal>
        <Statistic.Value>{this.state.count}</Statistic.Value>
        <Statistic.Label>{this.props.text}</Statistic.Label>
      </Statistic>
    );
  };
  render() {

    return this.renderContent();
  }
}

export default ChangableStatistic;
