import React from "react";
import { Chart } from "chart.js";
import generateCurrentTime from '../../../utils/generateCurrentTime';
class TimeSerie extends React.Component {
  chartRef = React.createRef();
  state = {
    data: [],
    labels: [],
  };
  componentDidMount() {
    this.updateTimeSerieEveryXSeconds();
  }

  updateTimeSerieEveryXSeconds = () => {
    setInterval(() => {
      let newData = Math.floor(Math.random() * 100);
      let newDatArray = [...this.state.data, newData];
      this.setState({ data: newDatArray });

      this.setState({ labels: [...this.state.labels, generateCurrentTime()] });

      const myChartRef = this.chartRef.current.getContext("2d");

      new Chart(myChartRef, {
        type: "line",
        data: {
          //Bring in data
          labels: this.state.labels,
          datasets: [
            {
              borderColor: "red",
              showLine: true,
              fill: false,
              label: "Customer in store",
              data: this.state.data,
            },
          ],
        },
        options: {
          scales: {
            yAxes: [
              {
                stacked: true,
                ticks: {
                  suggestedMin: 0,
                  suggestedMax: 100,
                  maxTicksLimit: 5,
                  beginAtZero: true,
                  stepSize: 0.5,
                },
              },
            ],
            xAxes: [],
          },
        },
      });
    }, 5000);
  };

  render() {
    return (
      <div>
          <h5>Number of customer in store every 5 seconds</h5>
        <canvas id="myChart" ref={this.chartRef} />
      </div>
    );
  }
}

export default TimeSerie;
