import React from "react";
import CornerIcon from "./CornerIcon";
import Feed from "./Feed";
import { Dropdown, Item, Image } from "semantic-ui-react";

const feeds = [
  {
    name: "Alan Schneider",
    subject: "Annual Financial Report",
    time: "5 sec",
  },
  {
    name: "Megan Seiru",
    subject: "Meeting at 5pm",
    time: "3 days",
  },
];

const Notification = () => (
  <div>
    <Dropdown icon={<CornerIcon />}>
      <Dropdown.Menu>
        <Dropdown.Header content={`${feeds.length} New messages from `} />
        {feeds.map((feed) => (
          <Feed {...feed} />
        ))}
      </Dropdown.Menu>
    </Dropdown>
  </div>
);

export default Notification;
