import React from "react";
import { Chart } from "chart.js";

class DoughnutChart extends React.Component {
  chartRef = React.createRef();

  componentDidMount() {
    this.updateTimeSerieEveryXSeconds();
  }

  updateTimeSerieEveryXSeconds = () => {
    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: "doughnut",
      data: {
        labels: ["Chocolate", "Vanilla Cream", "Cherry", "Coconut", "Raisin"],
        datasets: [
          {
            backgroundColor: ["#7c3c21", "#888888","#ea9a96",'#ec823a','#79d70f'],
            data: [30, 30, 20, 10, 5],
          },
        ],

      },
      options: {
        title: {
          display: true,
          text: '% of cookie Flavor'
        }
      }
    });
  };

  render() {
    return (
      <div style={{paddingBottom:'15px', marginBottom:'5px'}}>
        <h5>Cookie flavor preferences </h5>
        <canvas style={{backgroundColor:'#ebecf1'}} id="myChart" ref={this.chartRef} />
      </div>
    );
  }
}

export default DoughnutChart;
