import React from "react";
import { Chart } from "chart.js";

class TimeSerie extends React.Component {
  chartRef = React.createRef();

  componentDidMount() {
    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: "line",
      data: {
        //Bring in data
        // labels: ["Jan", "Feb", "March"],
        datasets: [
          {
            borderColor: "red",
            showLine: true,
            fill: false,
            label: "Customer in store",
            data: [
              {
                x: 10,
                y: 20,
              },
              {
                x: 15,
                y: 10,
              },
              { x: 45, y: 10 },
            ],
          },
        ],
      },
      options: {
        scales: {
          yAxes: [
            {
              stacked: true,
            },
          ],
        },
      },
    });
  }

  updateTimeSerieEveryXSeconds = () => {
    setInterval(() => {
      const myChartRef = this.chartRef.current.getContext("2d");

      new Chart(myChartRef, {
        type: "line",
        data: {
          //Bring in data

          datasets: [
            {
              borderColor: "red",
              showLine: true,
              fill: false,
              label: "Customer in store",
              data: [86, 67, 91, 92, 103],
            },
          ],
        },
        options: {
          scales: {
            yAxes: [
              {
                stacked: true,
              },
            ],
          },
        },
      });
    }, 2000);
  };

  render() {
    return (
      <div>
        <canvas id="myChart" ref={this.chartRef} />
      </div>
    );
  }
}

export default TimeSerie;
