import React from "react";
import { Grid, Image, List } from "semantic-ui-react";

export default () => {
  return (
    <Grid divided="vertically">
      <Grid.Row columns={1}>
        <Grid.Column>
          <div className="message">
            <div className="message-title">Chat message</div>
            <List className="message-list">
              <List.Item>
                <Image
                    style={{border:'1px solid red', width:'50px', borderRadius:'50%'}}

                  src="https://react.semantic-ui.com/images/avatar/small/rachel.png"
                />
                <List.Content>
                  <List.Header as="a">Rachel</List.Header>
                  <List.Description>
                    Good morning, We are having an unexpected large food order. There are not enough staffs to prepare the order.   
                  </List.Description>
                </List.Content>
              </List.Item>


            </List>
          </div>
        </Grid.Column>
      </Grid.Row>
    </Grid>
  );
};
