export default () => {
  return Math.floor(Math.random() * 100);
};

export const generateRandomNumberArray = (size, scale = 1000) => {
  if (size < 0) return new Error("invalid size");
  if (size === 1) return Math.floor(Math.random() * scale);
  else {
    const randomNumberArray = [];
    for (let i = 0; i < size; i++)
      randomNumberArray.push(Math.floor(Math.random() * scale));
    return randomNumberArray;
  }
};
