import React from "react";
import CornerIcon from "./CornerIcon";
import { Dropdown, Icon } from "semantic-ui-react";

const icons = [
  {
    mainIcon: "bell outline",
    cornerIcon: "circle red",
  },
];

dropdownIcon = <Icon color='red' name='users' />
const RingNotification = () => (
  <div className="inline-block notification">
    <Dropdown icon={<CornerIcon {...icons[0]} />}>
      <Dropdown.Menu>
        <Dropdown.Header content="You have 3 notifications" />
        <Dropdown.Divider />
        <Dropdown.Item icon={dropdownIcon} text="Important" />
        <Dropdown.Item icon="comment" text="Announcement" />
        <Dropdown.Item icon="conversation" text="Discussion" />
      </Dropdown.Menu>
    </Dropdown>
  </div>
);

export default RingNotification;
